<script>
import { required } from "vuelidate/lib/validators";
import { mapGetters } from "vuex";
export default {
  data() {
    return {
      image: "",
      form: {
        TeamName: "",
        TeamLogoPath: "",
        CreatedById: null,
        OrgId: null,
      },
      loader: false,
    };
  },
  validations: {
    form: {
      TeamName: {
        required,
      },
    },
  },
  computed: {
    ...mapGetters(["getUserId", "getOrganizationId"]),
  },
  methods: {
    resetForm() {
      this.form = {
        TeamName: "",
        TeamLogoPath: "",
        CreatedById: null,
        OrgId: null,
      };
      this.image = "";
    },
    fetchList() {
      this.$emit("fetchList");
    },
    close() {
      this.$emit("close");
      this.resetForm();
      this.$v.$reset();
    },
    handleImage(event) {
      const file = event.target.files[0];
      const fileReader = new FileReader();
      fileReader.onload = () => {
        this.image = fileReader.result;
      };
      fileReader.readAsDataURL(file);
      this.form.TeamLogoPath = file;
    },
    addTeam() {
      if (this.$v.$invalid) {
        this.$v.$touch();
      } else {
        this.form.CreatedById = this.getUserId;
        this.form.OrgId = this.getOrganizationId;
        this.loader = true;
        this.$store.dispatch("createTeam", this.form).then((response) => {
          if (response.data.succeeded === true) {
            this.loader = false;
            this.$swal.fire({
              position: "top-end",
              icon: "success",
              title: "Team added successfully",
              showConfirmButton: false,
              timer: 2000,
              toast: true,
            });
            this.close();
            this.fetchList();
            this.form = {
              TeamName: "",
              TeamLogoPath: "",
              BrokerId: null,
              CreatedById: null,
            };
            this.image = "";
          } else {
            this.loader = false;
            this.$swal.fire({
              position: "top-end",
              icon: "error",
              title: response.data.message,
              showConfirmButton: false,
              timer: 2000,
              toast: true,
            });
          }
        });
      }
    },
  },
};
</script>

